import React, { useContext } from "react";
import { ThemeContext } from "../../contexts/ThemeContext/ThemeContext";

const ToggleTheme = (props) => {
  const { styleSet } = props;
  const { theme, setTheme } = useContext(ThemeContext);

  window.onload = () => {
    if (theme === "dark") {
      document.body.style.background = "#111827"
    } else {
      document.body.style.background = "#E5E7EB"
    }
  }

  return (
    <>
      {theme === "dark" ? (
        <button onClick={() => { setTheme(theme === "dark" ? "light" : "dark"); document.body.style.background = "#E5E7EB" }}>
          <svg xmlns="http://www.w3.org/2000/svg" className={styleSet} viewBox="0 0 20 20" fill="#ffffff">
            <path fillRule="evenodd" d="M10 2a1 1 0 011 1v1a1 1 0 11-2 0V3a1 1 0 011-1zm4 8a4 4 0 11-8 0 4 4 0 018 0zm-.464 4.95l.707.707a1 1 0 001.414-1.414l-.707-.707a1 1 0 00-1.414 1.414zm2.12-10.607a1 1 0 010 1.414l-.706.707a1 1 0 11-1.414-1.414l.707-.707a1 1 0 011.414 0zM17 11a1 1 0 100-2h-1a1 1 0 100 2h1zm-7 4a1 1 0 011 1v1a1 1 0 11-2 0v-1a1 1 0 011-1zM5.05 6.464A1 1 0 106.465 5.05l-.708-.707a1 1 0 00-1.414 1.414l.707.707zm1.414 8.486l-.707.707a1 1 0 01-1.414-1.414l.707-.707a1 1 0 011.414 1.414zM4 11a1 1 0 100-2H3a1 1 0 000 2h1z" clipRule="evenodd" />
          </svg>
        </button>
      ) : (
        <button onClick={() => { setTheme(theme === "dark" ? "light" : "dark"); document.body.style.background = "#111827" }}>
          <svg xmlns="http://www.w3.org/2000/svg" className={styleSet} viewBox="0 0 20 20" fill="currentColor">
            <path d="M17.293 13.293A8 8 0 016.707 2.707a8.001 8.001 0 1010.586 10.586z" />
          </svg>
        </button>
      )}
    </>
  )
};

export default ToggleTheme;
